    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // textfield1 ve 2 ye giden yazıları al sayıya dönüştür. topla ve
        // label 3 de göster
        String s1, s2, s; // nesne tipi değişken
        double sayi1, sayi2, sonuc; // temel veri tipi, metotu yok!!
        s1 = jTextField1.getText(); // getter metot
        s2 = jTextField2.getText();
        sayi1 = Double.parseDouble(s1);
        sayi2 = Double.parseDouble(s2);  
        sonuc = sayi1 + sayi2;
        jLabel3.setText(String.valueOf(sonuc)); // setter metot
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // sayı1 den sayı2 çıkarıp label3 e yazalım
        double sayi1 = Double.parseDouble(jTextField1.getText());
        double sayi2 = Double.parseDouble(jTextField2.getText());
        double sonuc = sayi1 - sayi2;
        jLabel3.setText("Sonuç: "+sonuc); // üstü kapalı tip dönüşümü
                                          // imlicit cast
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // çarpma, nesne tipi değişkenler
        Double sayi1 = Double.parseDouble(jTextField1.getText());
        Double sayi2 = Double.parseDouble(jTextField2.getText());
        Double sonuc = sayi1 * sayi2;
        jLabel3.setText("Sonuç: "+sonuc);
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // Bölme temel veri tipi kullanarak valueof kullanarak
        double sayi1 = Double.valueOf(jTextField1.getText());
        double sayi2 = Double.valueOf(jTextField2.getText());
        double sonuc = sayi1 / sayi2;
        jLabel3.setText("Sonuç: "+String.format("%.2f",sonuc));
        // standart C de print("
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButtonAndActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAndActionPerformed
        // checkbox 1 ve 2 nin değerlerine VE operatörü uygulayacağız
        boolean A = jCheckBox1.isSelected();
        boolean B = jCheckBox2.isSelected(); // temel değişken
        boolean C = A && B; // VE                metot yok
        jLabelsonuc.setText("Sonuç: "+String.valueOf(C));
        jCheckBox3.setSelected(C);
    }//GEN-LAST:event_jButtonAndActionPerformed

    private void jButtonOrActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonOrActionPerformed
        // OR operatörü
        boolean A = jCheckBox1.isSelected();
        boolean B = jCheckBox2.isSelected();
        Boolean C = A || B; // OR nesne tipi boolean
        jLabelsonuc.setText("Sonuç: "+ C.toString());
        jCheckBox3.setSelected(C);
    }//GEN-LAST:event_jButtonOrActionPerformed

    private void jButtonExorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonExorActionPerformed
        // EXOR operatörü
        boolean A = jCheckBox1.isSelected();
        boolean B = jCheckBox2.isSelected();
        boolean C = A ^ B; // shift 3 boşluk
        jLabelsonuc.setText("Sonuç: "+C); // üstü kapalı tip dönüşümü
        jCheckBox3.setSelected(C);
    }//GEN-LAST:event_jButtonExorActionPerformed

    private void jButtonNotActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonNotActionPerformed
        // DEĞİL operatörü
        boolean A = jCheckBox1.isSelected();
        boolean B = !A;
        jLabelsonuc.setText("Sonuç: "+B); // üstü kapalı tip dönüşümü
        jCheckBox3.setSelected(B);
    }//GEN-LAST:event_jButtonNotActionPerformed

    private void jButtonBitselAndActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonBitselAndActionPerformed
        // 
        Integer A = Integer.parseInt(bitseltxt1.getText());
        Integer B = Integer.parseInt(bitseltxt2.getText());
        Integer C = A & B;
        // Bitwise işlemleri anlamak için sayıları ikilik tabanda görmeliyiz.
        bitseltxt1.setText(Integer.toBinaryString(A));
        bitseltxt2.setText(Integer.toBinaryString(B));
        bitseltxt3.setText(Integer.toBinaryString(C));
        //bitseltxt3.setText(C.toString());
    }//GEN-LAST:event_jButtonBitselAndActionPerformed

    private void jButtonBitselORActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonBitselORActionPerformed
        // bitsel veya. tam sayıları 2lik tabanda basamak basamak ve'liyoruz.
        Integer A = Integer.parseInt(bitseltxt1.getText());
        Integer B = Integer.parseInt(bitseltxt2.getText());
        Integer C = A | B;
        // bitsel veya operatörünün nasıl çalıştığını anlamak için
        // sayıları ikilik tabana çevirip basamak basamak 
        // veya işlemi uygulamamız gerekir.
        bitseltxt1.setText(Integer.toBinaryString(A));
        bitseltxt2.setText(Integer.toBinaryString(B));
        bitseltxt3.setText(Integer.toBinaryString(C));
    }//GEN-LAST:event_jButtonBitselORActionPerformed

