    private void jButtonBitselNOTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonBitselNOTActionPerformed
        // DEĞİL operatörü bitsel
        Integer A = Integer.parseInt(bitseltxt1.getText());
        Integer B = ~A;
        // bitsel veya operatörünün nasıl çalıştığını anlamak için
        // sayıları ikilik tabana çevirip basamak basamak 
        // "değil" işlemi uygulamamız gerekir.
        bitseltxt1.setText(Integer.toBinaryString(A));
        bitseltxt2.setText(Integer.toBinaryString(B));
    }//GEN-LAST:event_jButtonBitselNOTActionPerformed

